mapper.insert:

        	SELECT IFNULL(MAX(SEQ),0)+1 FROM GBL_BOARD_SAMPLE        

        INSERT INTO GBL_BOARD_SAMPLE (
        	 SEQ
        	,ID
			,NAME
			,DESCRIPTION
			,USE_YN
			,FILE_SEQ
			,REG_USER
			,REG_DATE
        ) VALUES (
        	 #{SEQ}
        	,#{ID}
			,#{NAME}
			,#{DESCRIPTION}
			,#{USE_YN}
			,#{FILE_SEQ}
			,#{REG_USER}
			,NOW()
        )

mapper.update:
UPDATE GBL_BOARD_SAMPLE SET
	 ID = #{ID}
	,NAME = #{NAME}
	,DESCRIPTION = #{DESCRIPTION}
	,USE_YN = #{USE_YN}

		,FILE_SEQ = #{FILE_SEQ}

	,REG_USER = #{REG_USER}
	,REG_DATE = NOW()
WHERE SEQ = #{SEQ}

mapper.select:
SELECT ID ,PASSWD ,NAME ,MEMBER_TYPE_CODE ,FIRST_CLASS_CODE ,MEMBER_CLASS_CODE ,JOIN_TYPE_CODE ,BIRTH_DAY ,BIRTH_TYPE_CODE ,GENDER ,PERSONAL_EMAIL ,PHONE ,MOBILE ,ZIPCODE ,BASIC_ADDRESS ,DETAIL_ADDRESS ,BANK_CODE ,ACCOUNT_NUMBER ,EMAIL_YN ,SMS_YN ,IPIN_NUMBER ,IPIN_SSN_HASH ,JOIN_DATE ,JOIN_IP ,MOD_DATE ,MOD_IP ,LOGIN_COUNT ,LATEST_LOGIN_DATE ,LATEST_LOGIN_IP ,FIRST_ORDER_YN ,FIRST_ORDER_DATE ,POINT ,SECEDE_YN ,SECEDE_TYPE_CODE ,SECEDE_DETAIL ,SECEDE_ID ,SECEDE_DATE ,SECEDE_IP ,BROKER_BGMALL_CODE ,GROUP_ID FROM GBL_MEMBER WHERE 1=1
AND ID = #{user_id}
AND NAME = #{user_nam}
