mapper.insert:

        	SELECT IFNULL(MAX(SEQ),0)+1 FROM GBL_BOARD_SAMPLE        

        INSERT INTO GBL_BOARD_SAMPLE (
        	 SEQ
        	,ID
			,NAME
			,DESCRIPTION
			,USE_YN
			,FILE_SEQ
			,REG_USER
			,REG_DATE
        ) VALUES (
        	 #{SEQ}
        	,#{ID}
			,#{NAME}
			,#{DESCRIPTION}
			,#{USE_YN}
			,#{FILE_SEQ}
			,#{REG_USER}
			,NOW()
        )

mapper.update:
UPDATE GBL_BOARD_SAMPLE SET
	 ID = #{ID}
	,NAME = #{NAME}
	,DESCRIPTION = #{DESCRIPTION}
	,USE_YN = #{USE_YN}

		,FILE_SEQ = #{FILE_SEQ}

	,REG_USER = #{REG_USER}
	,REG_DATE = NOW()
WHERE SEQ = #{SEQ}
